<template>
    <div class="plTableBox" :class="{uxbeautifyTableClass: beautifyTable}" :style="[plTableHeight]">
        <elx-grid ref="singleTable"
                  class="singleTable"
                  style="width: 100%"
                  :data="data"
                  :columns="columns"
                  :show-footer="summaryMethod ? showSummary : false"
                  :max-height="newMaxHeight"
                  :height="newHeight"
                  :mergeCells="mergeCells"
                  :validConfig="validConfig"
                  :auto-resize="widthResize"
                  :edit-rules="rules"
                  :border="beautifyTable ? false : border"
                  :scroll-y="scrollY"
                  :scroll-x="scrollX"
                  context-menu
                  :stripe="stripe"
                  :row-key="rowKey"
                  :column-key="columnKey"
                  :row-id="rowId"
                  :keep-source="keepSource"
                  :size="size"
                  :edit-config="editConfig"
                  :seq-config="indexConfig"
                  :expand-config="expandConfig"
                  :keyboard-config="keyboardConfig"
                  :mouse-config="mouseConfig"
                  :checkbox-config="checkboxConfig"
                  :tree-config="treeConfig"
                  :sort-config="sortConfig"
                  :show-header="showHeader"
                  :show-overflow="showOverflow"
                  :footer-method="footerMethod"
                  :show-header-overflow="showHeaderOverflow"
                  :show-footer-overflow="showFooterOverflow"
                  :highlight-current-row="highlightCurrentRow"
                  :highlight-hover-row="highlightCurrentRow"
                  :row-class-name="rowClassName"
                  :cell-class-name="cellClassName"
                  :header-row-class-name="headerRowClassName"
                  :header-cell-class-name="headerCellClassName"
                  :header-row-style="headerRowStyle"
                  :header-cell-style="headerCellStyle"
                  :row-style="rowStyle"
                  :cell-style="cellStyle"
                  :span-method="spanMethod ? arraySpanMethod: null"
                  @current-change="currentChange"
                  @checkbox-change="plSelect"
                  @resizable-change="headerDragend"
                  @cell-mouseenter="cellMouseEnter"
                  @cell-mouseleave="cellMouseLeave"
                  @cell-click="cellClick"
                  @cell-dblclick="cellDblclick"
                  @cell-context-menu="rowContextmenu"
                  @header-cell-context-menu="headerContextmenu"
                  @radio-change="radioChange"
                  @header-cell-click="headerClick"
                  @checkbox-all="selectAll"
                  @filter-change="filterChange"
                  @sort-change="sortChange"
                  @edit-closed="editClosed"
                  @edit-actived="editActived"
                  @edit-disabled="editDisabled"
                  @toggle-tree-expand="toggleTreeExpandEv"
                  @toggle-row-expand="expChang"
                  @keydown="keydown"
                  @edit-currently-active="editCurrentlyActive"
                  @scroll="tableBodyScroll">
            <!--无数据布局-->
            <template slot="empty">
                <slot name="empty">{{ emptyText }}</slot>
            </template>
            <slot/>
        </elx-grid>
        <div class="myPagination" v-if="paginationShow" ref="myPagination">
            <el-pagination
                    @size-change="handleSizeChange"
                    @current-change="handleCurrentChange"
                    :current-page="newcurrentPage"
                    :pager-count="pagerCount"
                    :page-sizes="pageSizes"
                    :page-size="newPageSize"
                    :layout="layout"
                    :total="total"/>
        </div>
        <div class="plDialog" ref="plDialog">
            <div style="width: 100%;height: 100%;" v-if="plDialogFals">
                <div class="table-cus-header">{{ fieldTitle }}</div>
                <div class="checkListBox">
                    <draggable tag="ul"
                               :options="{disabled: !fieldSort}"
                               v-model="newDialogData">
                        <li v-for="(item, index) in newDialogData" :key="index">
                            <el-checkbox v-model="item.state" :disabled="item.disabled">{{ item.name }}</el-checkbox>
                            <template v-if="showDialogIcon">
                                <i class="iconfont"
                                   :class="[moveDownIcon || 'el-icon-bottom']"
                                   @click="handleField('下移', index, newDialogData)"
                                   v-if="index !== newDialogData.length - 1"/>
                                <i class="iconfont"
                                   :class="[moveUpIcon || 'el-icon-top']"
                                   @click="handleField('上移', index, newDialogData)"
                                   v-if="index !== 0"/>
                            </template>
                            <i class="iconfont"
                               :class="[amendBtnIcon || 'el-icon-edit']"
                               v-if="showAmend"
                               @click="amendField(item, index)"/>
                        </li>
                    </draggable>
                </div>
                <div class="table-cus-footer">
                    <el-button @click="closeModal()">取消</el-button>
                    <el-button type="primary" @click="confirmField">确定</el-button>
                    <el-button type="warning" @click="reset">重置</el-button>
                </div>
            </div>
        </div>
    </div>
</template>

<script type="text/babel">
    import Vue from 'vue'
    import draggable from 'vuedraggable'
    import ElPagination from 'umy-table/packages/pagination'
    import ElCheckbox from 'umy-table/packages/checkbox'
    import ElButton from 'umy-table/packages/button'
    import uxGridMixin from '../mixins'
    import { parseHeight, isArrayFn } from '../../../utils/common'
    export default {
        name:'UxGrid',
        mixins: [uxGridMixin],
        components: {draggable, ElPagination, ElCheckbox, ElButton},
        data () {
            return {
                _times: '', // 弹框定时器
                plDialogFals: false, // 侧滑弹框是否开启
                newDialogData: [], // 新自定义字段的数据
                aBox: '', // 蒙层节点
                newPageSize: '', // 每页条数
                newcurrentPage: '', // 当前页
                newHeight: null, // 表格高度
                newMaxHeight: null // 表格最大高度
            }
        },
        created () {
            this.newPageSize = this.pageSize
            this.newcurrentPage = this.currentPage
        },
        mounted () {
            this.$ready = true
        },
        methods: {
            // 自定义字段按钮事件
            handleField (name, index, arr) {
                switch (name) {
                    case '上移':
                        arr = this.swapItems(arr, index, index - 1)
                        break
                    case '下移':
                        arr = this.swapItems(arr, index, index + 1)
                        break
                    default:
                        break
                }
                this.$emit('handle-field', name, index)
            },
            // 交换后的数据
            swapItems(arr, index1, index2) {
                arr[index1] = arr.splice(index2, 1, arr[index1])[0];
                return arr;
            },
            // 设置表格高度
            setHeight () {
                if (!this.$ready && this.paginationShow) return Vue.nextTick(() => this.setHeight());
                const { myPagination } = this.$refs;
                if (this.height) {
                    this.newHeight = parseHeight(this.height) - (myPagination ? myPagination.offsetHeight : 0)
                    this.newMaxHeight = null
                } else if (this.maxHeight) {
                    this.newMaxHeight = parseHeight(this.maxHeight) - (myPagination ? myPagination.offsetHeight : 0)
                    this.newHeight = null
                }
            },
        },
        computed: {
            plTableHeight() {
                if (this.height) {
                    return {
                        height: this.height + 'px'
                    };
                } else if (this.maxHeight) {
                    const maxHeight = parseHeight(this.maxHeight);
                    if (typeof maxHeight === 'number') {
                        return {
                            'max-height': this.maxHeight + 'px'
                        };
                    }
                }
                return {};
            }
        },
        watch: {
            data: {
                immediate: true,
                handler (val) {
                    if (!isArrayFn(val)) {
                        throw new Error('表格数据需要的是数组格式，请检查你的数据格式');
                    }
                }
            },
            dialogData: {
                deep: true,
                immediate: true,
                handler (val) {
                    this.newDialogData = JSON.parse(JSON.stringify(val))
                }
            },
            currentPage (val) {
                this.newcurrentPage = val
            },
            pageSize (val) {
                this.newPageSize = val
            },
            height: {
                immediate: true,
                handler(value) {
                    this.setHeight();
                }
            },
            maxHeight: {
                immediate: true,
                handler(value) {
                    this.setHeight();
                }
            }
        }
    }
</script>
